gameforge["de"][2705] = "Affendungeon "
gameforge["de"][2706] = "Sie alle werden den Tod finden! "
gameforge["de"][2707] = "Kleine, kleine, kleine Stücke... "
gameforge["de"][2708] = "Kein Entkommen! "
gameforge["de"][2709] = "Dein Blut wird den Boden tränken. Stirb! "
gameforge["de"][2710] = "Bettle, aber es wird dir nichts nützen! "
gameforge["de"][2711] = "Du kannst deine Furcht nicht vor mir verbergen! "
gameforge["de"][2712] = "Tod! Tod allen Menschen... "
gameforge["de"][2713] = "Ihr armseligen Würmer! "
gameforge["de"][2714] = "So langsam machst du mich wütend... "
gameforge["de"][2716] = "Dein Blut wird fließen, Mensch. "
gameforge["de"][2717] = "Tod und Verderben...! "
gameforge["de"][2718] = "Hast du schon einmal die Wunden gesehen, die ein Speer reißen kann? "
gameforge["de"][2719] = "Ja, lauf ruhig davon! Wir werden sehen, wie weit du kommst... "
gameforge["de"][2720] = "Allein der Anblick deiner zitternden Hände ist diese Begegnung wert... "
gameforge["de"][2721] = "Du Abschaum wagst es, mich herauszufordern? "
gameforge["de"][2722] = "Der Tod wird zukünftig dein einziger Gefährte sein! "
gameforge["de"][2723] = "Deine Seele wird mir gehören, Mensch! "
gameforge["de"][2724] = "Ah, Frischfleisch! "
gameforge["de"][2725] = "Es sieht aus, als würde heute ein weiterer Mensch hier den Tod finden! "
gameforge["de"][2726] = "Leiden sollst du, leiden und all deine Qualen laut herausschreien... "
gameforge["de"][2727] = "Denkst du etwa, die lächerliche Waffe dort in deiner Hand wird dich schützen können? "
gameforge["de"][2728] = "Dein Mut hat dich zu mir gebracht. Nun wird er dich verlassen... "
gameforge["de"][2729] = "Sollte ich sterben, wird dich meine Rache ewig verfolgen! "
gameforge["de"][2730] = "Ich werde dich langsam sterben lassen, sehr langsam... "
gameforge["de"][2731] = "Es wird mir ein Vergnügen sein, dich leiden zu lassen. "
gameforge["de"][2732] = "Dein Körper zittert wie der eines altersschwachen Weibes! "
gameforge["de"][2733] = "Ich kann deine Angst riechen, kleiner Mensch. "
gameforge["de"][2734] = "Ich denke es ist Zeit, dich um einige Körperteile zu erleichtern. "
gameforge["de"][2735] = "Ich werde dich in deine persönliche Hölle schicken! "
gameforge["de"][2736] = "Deine Schmerzen werden dich in den Wahnsinn treiben, noch ehe du stirbst... "
gameforge["de"][2737] = "Jammere! Flehe! Aber es wird dir nichts nützen. "
gameforge["de"][2738] = "Du wirst den schlimmsten Tod erleiden, den du dir vorstellen kannst... "
gameforge["de"][2740] = "Deine Kraft lässt nach. Du stehst bereits an der Schwelle des Todes... "
gameforge["de"][2741] = "Bereit, auf ewig in den Hallen des Todes zu wandeln? "
gameforge["de"][2742] = "Du solltest dich mit dem Gedanken anfreunden, die ewige Dunkelheit zu begrüßen... "
gameforge["de"][2743] = "Willkommen in der Hölle! "
gameforge["de"][2744] = "Schrei, soviel du willst! Hier hört dich niemand! "
gameforge["de"][2745] = "Wenn du glaubst, mich auf diese Weise töten zu können... dann bist du naiver, als du aussiehst! "
gameforge["de"][2746] = "Kein übler Angriff, aber vor dem Tod wird auch er dich nicht schützen können. "
gameforge["de"][2747] = "Sieh dich um, dies ist deine Hölle! "
gameforge["de"][2748] = "Ich werde dir dein Fleisch von den Knochen schälen. "
gameforge["de"][2749] = "Ich will dich wimmern hören, ehe du stirbst. "
gameforge["de"][2750] = "Du weißt bereits, dass jegliche Hoffnung vergebens ist, nicht wahr? "
gameforge["de"][2752] = "Nein, noch stirbst du nicht. Zunächst will ich wissen, wie viele Schwerthiebe dein Körper verkraften kann... "
gameforge["de"][2753] = "Ah, ein neuer Freiwilliger... "
gameforge["de"][2754] = "Glaubst du wirklich, auch nur die geringste Chance zu haben? "
gameforge["de"][2755] = "Das ist nicht der Boden, der sich bewegt... deine Knie schlottern vor Angst! "
gameforge["de"][2756] = "Willkommen an dem Ort, an dem du deinen Tod finden wirst! "
gameforge["de"][2757] = "Ich kann die Panik in deinen Augen leuchten sehen. "
gameforge["de"][2758] = "Oh, so jung und hilflos... "
gameforge["de"][2759] = "Wir sehen jede noch so kleine Bewegung deines Körpers. "
gameforge["de"][2760] = "Eile? Wünschst du deinen Tod so sehr herbei? "
gameforge["de"][2761] = "Der Tod hat bereits seinen Platz an deiner Seite eingenommen... "
gameforge["de"][2762] = "Hörst du mein Glöckchen läuten? Es ist das letzte, das du hören wirst! "
gameforge["de"][2763] = "Ich rieche Blut, viel Blut... lange wirst du so nicht mehr überleben... "
gameforge["de"][2764] = "Du kannst kämpfen... oder dich sofort vom Tod umarmen lassen... "
gameforge["de"][2765] = "Warum bist du hier, wenn du so sehr an deinem Leben hängst? "
gameforge["de"][2766] = "Ah, neue Körper, die ich zerschmettern kann! "
gameforge["de"][2767] = "Es war deine Entscheidung herzukommen, Mensch! Nun lebe mit den Konsequenzen... "
gameforge["de"][2768] = "Unsere Kampfkraft wird niemals nachlassen. Wir können nicht sterben! "
gameforge["de"][2769] = "Es macht keinen Sinn, uns anzugreifen! Du bist verloren... "
gameforge["de"][2770] = "Vielleicht wirst du an Erschöpfung sterben... oder an Angst... "
gameforge["de"][2771] = "Du hast es bis zu mir geschafft... und ich werde dich nun in den Tod schicken. "
gameforge["de"][2772] = "Opfere deine Seele und du kannst einer von uns werden! "
gameforge["de"][2773] = "Sieh dich an! Schwach und langsam... "
gameforge["de"][2774] = "Dein Körper und deine Seele gehören mir! "
gameforge["de"][2775] = "Deine Beine zittern! Sieh es ein, du bist dem hier nicht gewachsen... "
gameforge["de"][2776] = "Wie lange willst du das noch durchhalten? "
gameforge["de"][2777] = "Wer konnte ahnen, dass es etwas Schlimmeres als den Tod gibt? "
gameforge["de"][2778] = "Willkommen, Todeshungriger. "
gameforge["de"][2779] = "Für deinen Leichtsinn wirst du bezahlen. Mit deinem Blut und deiner Seele. "
gameforge["de"][2780] = "Nachdem ich deinen Körper zerstört habe, wird deine Seele mir gehören! "
gameforge["de"][2781] = "Wie lange, denkst du, kannst du gegen mich bestehen? "
gameforge["de"][2782] = "Du amüsierst mich. Deine Angst und deine Hast sind unterhaltsam... "
gameforge["de"][2783] = "Hört alle zu, die ihr euch Sorgen um eure Zukunft macht! "
gameforge["de"][2784] = "Wenn du die Macht der Natur und insbesondere der Blitze erlangen willst, empfehle ich die Lehre der Heilung! "
gameforge["de"][2785] = "Das Besondere am Krieger ist seine Einstellung zum Kampf. Konzentriere dich darauf, deinen Körper zu trainieren und dich vorzubereiten! "
gameforge["de"][2786] = "Die Stärke des Sura ist die Magie. Arbeite an deiner Intelligenz und Schnelligkeit, aber vernachlässige deine Körperkraft nicht! "
gameforge["de"][2787] = "Nutze die Eigenschaften der Ninja! Konzentriere dich auf Schnelligkeit und Körperkraft! "
gameforge["de"][2788] = "Schamanen nutzen Wiederherstellung, Unterstützung und Fernkampfzauber. Dafür ist Intelligenz unerlässlich! "
gameforge["de"][2789] = "Vergiss nicht das Wissen, das du in deiner Ausbildung erhältst! "
gameforge["de"][2790] = "Mach eine Ausbildung, wenn du deine Fertigkeiten verbessern willst! "
gameforge["de"][2791] = "Fertigkeiten? Du kannst bis zum Level des Meisters aufsteigen, mit zufälliger Verteilung zwischen Level 17 und Level 21! "
gameforge["de"][2792] = "Du möchtest Großmeister werden? Dann benötigst du ein besonderes Fertigkeitsbuch! "
gameforge["de"][2793] = "Sobald du ein Fertigkeitsbuch gelesen hast, verschwindet es! "
gameforge["de"][2794] = "Alle Kämpfer bis Level 5, hört mich an! "
gameforge["de"][2795] = "Du möchtest ein weiteres Fertigkeitsbuch lesen? Dann warte einen Tag. "
gameforge["de"][2796] = "Steigere deine Intelligenz, um deine Sura-Fertigkeiten zu verbessern! "
gameforge["de"][2797] = "Erlebe die große Macht eines Sura im Kampf! "
gameforge["de"][2798] = "Weißt du, wo sich der Tempel der esoterischen Sekte befindet? Er liegt mitten im Tal von Seungryong! "
gameforge["de"][2799] = "Die Spinnenhöhlen sind wieder aufgetaucht. Ihre Eingänge liegen in der Yongbi-Wüste! "
gameforge["de"][2800] = "Erledige Aufträge und nutze die Belohnungen zur Verbesserung! "
gameforge["de"][2801] = "Angeln? Das klingt gut! Trägst du bereits eine Angelrute und einen Köder bei dir? "
gameforge["de"][2802] = "Suchst du eine größere Herausforderung beim Jagen? Mit der Hilfe eines Portals kannst du zu einem Jagdort gelangen, der deinem Level entspricht! "
gameforge["de"][2803] = "Nimm dich vor Feinden aus anderen Ländern in Acht. Man kann nie wissen, wann sie einem in den Rücken fallen! "
gameforge["de"][2804] = "Du möchtest deine Fertigkeiten erneuern? Dann begib dich zu der alten Frau! Aber sorge dafür, dass du genug Yang dabei hast. "
gameforge["de"][2805] = "Bewirb dich beim Nahkampf-Lehrer, wenn du als angesehener Angriffskrieger bekannt werden willst! "
gameforge["de"][2806] = "Strebe das höchste Level an - das des Meisters. So wird dein Name gewiss die Zeit überdauern! "
gameforge["de"][2807] = "Wenn du eine andere Stadt oder ein Kampfgebiet betreten möchtest, dann such auf den Kontinenten nach alten Menschen! "
gameforge["de"][2808] = "Wenn du dich in den Fertigkeiten des Kriegers üben möchtest, gehe und lerne vom Meister der Kampfkunst. "
gameforge["de"][2809] = "Wenn du ein Sura werden willst, der magische Schwerter beherrscht, dann wähle die Lehre der magischen Waffen! "
gameforge["de"][2810] = "Wenn du stark im Einsatz dunkler Zauber werden möchtest, begib dich in die Lehre der schwarzen Magie! "
gameforge["de"][2811] = "Die Fernkampf-Lehre richtet sich an Ninja, die mindestens Level 5 erreicht haben! "
gameforge["de"][2812] = "Wähle die Nahkampf-Lehre, wenn du in die geheimen Ränge der Ninja aufsteigen willst! "
gameforge["de"][2813] = "Wenn du als Krieger des Drachengottes in die Schlacht ziehen möchtet, so wähle die göttliche Drachenmacht-Lehre! "
gameforge["de"][2814] = "Hört alle zu, die Ihr euch Sorgen um eure Gesundheit macht! "
gameforge["de"][2815] = "Wenn du die Macht der Natur und insbesondere die Kraft der Blitze beherrschen willst, dann solltest du dich der Heilungs-Lehre zuwenden! "
gameforge["de"][2816] = "Das Besondere am Krieger ist seine Art, Kämpfe anzugehen. Konzentriere dich darauf, die Fähigkeiten deines Körpers zu verbessern! "
gameforge["de"][2817] = "Die Stärke des Sura ist die Magie. Arbeite an deiner Intelligenz, Schnelligkeit und Körperkraft! "
gameforge["de"][2818] = "Führe die typischen Fähigkeiten des Ninja auf ein höheres Level! Konzentriere dich auf Schnelligkeit und Körperkraft! "
gameforge["de"][2819] = "Schamanen nutzen Wiederherstellung und Fernkampfzauber zur Unterstützung im Kampf. Dafür ist Intelligenz unerlässlich! "
gameforge["de"][2820] = "Vergiss nicht, was du gelernt hast! "
gameforge["de"][2821] = "Tritt eine Lehre an, wenn du deine Fertigkeiten zu verbessern wünschst! "
gameforge["de"][2822] = "Fertigkeiten? Du kannst bis zum Level des Meisters aufsteigen, mit zufälliger Verteilung zwischen Level 17 und Level 21! "
gameforge["de"][2823] = "Du möchtest Großmeister werden? Dann benötigst du ein besonderes Fertigkeitsbuch! "
gameforge["de"][2824] = "Sobald du ein Fertigkeitsbuch gelesen hast, verschwindet es! "
gameforge["de"][2825] = "Alle Kämpfer bis Level 5, hört mich an! "
gameforge["de"][2826] = "Du möchtest ein weiteres Fertigkeitsbuch lesen? Dann warte einen Tag. "
gameforge["de"][2827] = "Steigere deine Intelligenz, um deine Sura-Fertigkeiten zu verbessern! "
gameforge["de"][2828] = "Erlebe die große Macht eines Sura im Kampf! "
gameforge["de"][2829] = "Weißt du, wo sich der Tempel der esoterischen Sekte befindet? Er liegt mitten im Tal von Seungryong! "
gameforge["de"][2830] = "Die Spinnenhöhlen sind wieder aufgetaucht. Ihre Eingänge liegen in der Yongbi-Wüste! "
gameforge["de"][2831] = "Erledige Aufträge und nutze die Belohnungen zur Verbesserung! "
gameforge["de"][2832] = "Angeln? Das klingt gut! Trägst du bereits eine Angelrute und einen Köder bei dir? "
gameforge["de"][2833] = "Suchst du eine größere Herausforderung beim Jagen? Mit der Hilfe eines Portals kannst du zu einem Jagdort gelangen, der deinem Level entspricht! "
gameforge["de"][2834] = "Nimm dich vor Feinden aus anderen Ländern in Acht. Man kann nie wissen, wann sie einem in den Rücken fallen! "
gameforge["de"][2835] = "Du möchtest deine Fertigkeiten erneuern? Dann begib dich zu der alten Frau! Aber sorge dafür, dass du genug Yang dabei hast. "
gameforge["de"][2836] = "Bewirb dich beim Nahkampf-Lehrer, wenn du als angesehener Angriffskrieger bekannt werden willst! "
gameforge["de"][2837] = "Strebe das höchste Level an - das des Meisters. So wird dein Name gewiss die Zeit überdauern! "
gameforge["de"][2838] = "Wenn du eine andere Stadt oder ein Kampfgebiet betreten möchtest, dann such auf den Kontinenten nach alten Menschen! "
