gameforge["de"][7402] = "Das Moschusöl "
gameforge["de"][7403] = "Ich danke dir von Herzen. Nun kann ich endlich[ENTER]ein neues Duftöl herstellen. "
gameforge["de"][7404] = "Du kannst meinem Singvogel ausrichten, dass ich[ENTER]heute wieder vor ihrem Fenster singen werde. Sie[ENTER]wird sich freuen. "
