gameforge["de"][7417] = "Das Moschusöl "
gameforge["de"][7418] = "Ein neuer Duft "
gameforge["de"][7419] = "Jage Blauwölfe "
gameforge["de"][7420] = "Waffenhändler "
gameforge["de"][7421] = "Zurück zum Waffenhändler "
