gameforge["hu"][2722] = "A halál lesz az egyetlen társad ezentúl! "
gameforge["hu"][2723] = "Enyém lesz a lelked, ember! "
gameforge["hu"][2724] = "Ah, friss vér! "
gameforge["hu"][2725] = "Úgy tûnik, újabb személy akar itt megismerkedni a halállal! "
gameforge["hu"][2726] = "Szenvedned kellene, üvöltened a kíntól... "
gameforge["hu"][2727] = "Te tényleg azt hiszed, hogy meg tudod védeni magad azzal a nevetséges fegyverrel a kezedben? "
gameforge["hu"][2728] = "A bátorságod vezetett ide hozzám. És most el fog hagyni... "
gameforge["hu"][2729] = "Ha meghalok, bosszúm üldözni fog az örökkévalóságig! "
