gameforge["hu"][2731] = "Örömet okoz, ha látlak majd szenvedni. "
gameforge["hu"][2732] = "Tested reszket, mint a nyárfalevél! "
gameforge["hu"][2733] = "Érzem a félelmed, kicsim. "
gameforge["hu"][2734] = "Azt hiszem, eljött az ideje, hogy megszabadulj néhány testrészedtõl. "
gameforge["hu"][2735] = "Saját személyes poklodba küldelek! "
gameforge["hu"][2736] = "Az õrületbe fog kergetni a fájdalmad, mielõtt meghalsz... "
gameforge["hu"][2737] = "Jajgathatsz! Könyöröghetsz! De semmi nem segíthet rajtad. "
gameforge["hu"][2738] = "Megtapasztalod az elképzelhetõ legrosszabb halált! "
